import "../styles/UserSearch.css"
import { Typography } from "@mui/material";
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import TextField from '@mui/material/TextField';
import {useEffect, useState} from 'react';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';

const UserSearch = () => {
  const [errMsg, setErrMsg] = useState('')
  const [isLoading, setIsLoading] = useState(false)
  const [data, setData] = useState([])
  const [enteredUserName, setEnteredUserName] = useState()
  const [isBtnDisabled, setBtnDisabled] = useState('disabled')
  
  const handleDisableBtn = (e)=> {
    if(e.target.value){
      setEnteredUserName(e.target.value)
      setBtnDisabled('contained')
    }else{
      setData('')
      setBtnDisabled('disabled')
    }
  }

  const handleSubmit = (e) => {
    setBtnDisabled('disabled')
    e.preventDefault()
    
    fetch("https://analyze-coderhub.herokuapp.com/user_data/"+String(enteredUserName))
    .then((res) => {
      return res.json()})
    .then(data => {
      setData([Object.values(data.easy_solved)])})
    }


    return (
      <>
        <Box
          sx={{
            py: 3,
            mt:15
          }}
          style={{textAlign: "center"}}
        >
          <Container maxWidth="lg">
            <Grid
              container
              spacing={1}
              justifyContent="center"
            >
              <Grid
                item
                lg={12}
                md={6}
                xs={12}
              >
                <Typography 
                  variant="h2"

                >
                search for a user by username.
                </Typography>                
              </Grid>
              <Grid
                item
                xs={12}
              >
                <form noValidate autoComplete="off" onSubmit={handleSubmit} >
                  <Grid>
                  <TextField 
                    id="outlined-basic" 
                    label="username" 
                    variant="outlined"
                    required
                    onChange= {(e) => handleDisableBtn(e)}
                  />
                  <Typography 
                    variant="p"
                    color='error'
                  >
                  {errMsg}
                  </Typography>
                  </Grid>
                  <Grid>
                  <Button
                      variant={isBtnDisabled}
                      type='submit'
                      color="primary"
                      sx={{mt:3}}
                  >
                  Search</Button>
                  </Grid>
                </form>
              </Grid>
            </Grid>
          </Container>
        </Box>
      </>
    );
}
export default UserSearch;
