  var bounds = this.radius.bounds;

  if (out) {
    out[0] = Math.exp(bounds[0][0]);
    out[1] = Math.exp(bounds[1][0]);
    return out;
  }

  return [Math.exp(bounds[0][0]), Math.exp(bounds[1][0])];
