  this.recalcMatrix(this.lastT());
  return {
    center: this.computedCenter.slice(),
    rotation: this.computedRotation.slice(),
    distance: Math.log(this.computedRadius[0]),
    zoomMin: this.radius.bounds[0][0],
    zoomMax: this.radius.bounds[1][0]
